update product
set product_name = #{product_name}, product_explain = #{product_explain},
	product_price = #{product_price}, product_cate = #{product_cate},
	product_count = #{product_count}, product_size=#{product_size}
	where product_id= #{product_id}

update store_qna 
set qna_subject = #{subject}, qna_content = #{content}
where qna_id = ${no}

mapper.select:
select qna_id as no, qna_subject as subject, qna_content as content
from store_qna
where qna_id=${_parameter}

